class recipeEdit extends React.Component {

    render() {
    const editRecipe = Object.entries(this.props.edit).map ( ([key, value]) => {
        return (
            <div>
                <label>{key}:</label>
                <br/>
                <input type='test' name={key} value={value}/>
                <br/>
